package com.class03;

public class DoubleComparison {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		double practice=1997;
		double practice2=2001;
		
		if(practice>practice2) {
			System.out.println("Double value "+practice+" is greater than "+practice2+".");
		}
		else {
			System.out.println("Double value "+practice+" is less than "+practice2+".");
		}
		
	}

}
