package com.class11;

public class GroupHWNumber1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int[][] a = {
			    {10,15,20}, 
			    {30,10,20}, 
			    {0,1,2}  
			  };
			  int sum=0;
			  for (int r=0; r<a.length;r++){
			   for (int l=0;l<a[r].length;l++){
			    sum+=a[r][l];
			   }
			   System.out.println(sum);
			 
			  }
			  
	          }
			  
	       }
			
		
	
	


